import service from '../index.js';
import Cookies from 'js-cookie';
//获取首页轮播图
export function getBanner() {
    return service({
        method: 'GET',
        url: '/banner?type=2'
    })
}
//获取发现好歌单
export function getMusicList() {
    return service({
        method: 'GET',
        url: '/personalized?limit=10'
    })
}
export function getSearchResult(data) {
    return service({
        method: 'GET',
        url: `/cloudsearch?keywords=${data}`
    })
}
export function getCode(data) {
    return service({
        method: 'GET',
        url: `/login/qr/key?timeStamp=${data}`
    })
}
export function getQRCode(data) {
    return service({
        method: 'GET',
        url: `/login/qr/create?key=${data.data.data.unikey}&qrimg=${data.data.data.unikey}`
    })
}
export function getQRresult(data,time) {
    return service({
        method: 'GET',
        url: `/login/qr/check?key=${data}&time=${time}`
    })
}
export function likeMusic(data) {
    return service({
        method: 'GET',
        url: `/like?id=${data}`,
        withCredentials: true,
        headers:{
            'Cookie': Cookies.get('user')
        }
    })
}
