import { createStore } from 'vuex'

export default createStore({
  state: {
    playList: [{
      al: {
        "id": 36322576,
        "name": "如行于野",
        "picUrl": "https://p2.music.126.net/w6YUSoitoUNhTnlkfSStlg==/109951164452946232.jpg",
        "tns": [],
        "pic_str": "109951164452946232",
        "pic": 109951164452946240,
      },
      id: 508672488,
    }],
    playListIndex:0,//默认下标
    isbtnShow:true,//播放按钮的显示
    currentTime:0,
    duration:0,
  },
  getters: {
  },
  mutations: {
    updateIsbtnShow:function(state,value){
      state.isbtnShow=value;
    },
    updateAllPlayList:function(state,value){
      state.playList = value;
    },
    updatePlayListIndex:function(state,value){
      state.playListIndex = value;
    },
    updatePlayList:function(state,value){
      state.playList.push(value);
      this.updatePlayListIndex(state.playListIndex+1);
      console.log(state.playList);
    },
    updatePlayIndex:function(state,value){
      console.log(value);
      state.playListIndex = value;
    },
    updateDuration:function(state,value){
      state.duration=value;
      console.log("当前持续时间:"+value);
    },
    updateCurrentTime:function(state,value){
      state.currentTime = value;
    }
  },
  actions: {
  },
  modules: {
  }
})
